import React, { useState } from 'react';
import './App.css';

function App() {
  const [isLogin, setIsLogin] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [message, setMessage] = useState('');
  const [user, setUser] = useState(null);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage('–û—Ç–ø—Ä–∞–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö...');

    try {
      const response = await fetch('http://localhost:4000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        setMessage(`‚úÖ ${data.message}`);
        setUser(data.user);
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (error) {
      setMessage('‚ùå –û—à–∏–±–∫–∞ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è –∫ —Å–µ—Ä–≤–µ—Ä—É');
      console.error('–û—à–∏–±–∫–∞:', error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage('–í—Ö–æ–¥ –≤ —Å–∏—Å—Ç–µ–º—É...');

    try {
      const response = await fetch('http://localhost:4000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password
        }),
      });

      const data = await response.json();

      if (data.success) {
        setMessage(`‚úÖ ${data.message}`);
        setUser(data.user);
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (error) {
      setMessage('‚ùå –û—à–∏–±–∫–∞ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è –∫ —Å–µ—Ä–≤–µ—Ä—É');
      console.error('–û—à–∏–±–∫–∞:', error);
    }
  };

  if (user) {
  return (
    <div className="App">
      <div className="header">
        <div className="logo">
          <h1 className="gothic-title">MushCase</h1>
          <div className="blood-drip"></div>
        </div>
        <div className="user-panel">
          <span className="username">üë§ {user.username}</span>
          <span className="balance">üí∞ {user.balance}‚ÇΩ</span>
          <button onClick={() => setUser(null)} className="logout-btn">–í—ã–π—Ç–∏</button>
        </div>
      </div>

      <div className="main-content">
        <div className="promo-banner">
          <h2>üî• –û—Ç–∫—Ä—ã–≤–∞–π –∫–µ–π—Å—ã CS2 –∏ –≤—ã–∏–≥—Ä—ã–≤–∞–π —Ä–µ–¥–∫–∏–µ —Å–∫–∏–Ω—ã!</h2>
        </div>

        <div className="cases-grid">
          {cases.map(caseItem => (
            <div key={caseItem.id} className="case-card">
              <div className="case-image">
                <img src={caseItem.image} alt={caseItem.name} />
              </div>
              <h3 className="case-name">{caseItem.name}</h3>
              <p className="case-price">{caseItem.price}‚ÇΩ</p>
              <button
                className="open-case-btn"
                onClick={() => openCase(caseItem)}
                disabled={user.balance < caseItem.price}
              >
                {user.balance >= caseItem.price ? '–û—Ç–∫—Ä—ã—Ç—å –∫–µ–π—Å' : '–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤'}
              </button>
            </div>
          ))}
        </div>

        <div className="bottom-panel">
          <button className="inventory-btn" onClick={() => setShowInventory(!showInventory)}>
            üéí –ò–Ω–≤–µ–Ω—Ç–∞—Ä—å ({inventory.length})
          </button>
          <button className="add-balance-btn" onClick={addBalance}>
            üí≥ –ü–æ–ø–æ–ª–Ω–∏—Ç—å –±–∞–ª–∞–Ω—Å
          </button>
        </div>

        {showInventory && (
          <div className="inventory">
            <h3>üéí –í–∞—à –∏–Ω–≤–µ–Ω—Ç–∞—Ä—å:</h3>
            <div className="inventory-grid">
              {inventory.map(item => (
                <div key={item.id} className="inventory-item">
                  <img src={item.image} alt={item.name} />
                  <p>{item.name}</p>
                  <p>{item.price}‚ÇΩ</p>
                  <button onClick={() => sellItem(item)}>–ü—Ä–æ–¥–∞—Ç—å</button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

  return (
    <div className="App">
      <header className="App-header">
        <div className="logo">
          <h1 className="gothic-title">MushCase</h1>
          <div className="blood-drip"></div>
        </div>
        <p className="subtitle">CS2 Case Opening</p>

        <div className="auth-form">
          <div className="tabs">
            <button
              className={!isLogin ? 'tab active' : 'tab'}
              onClick={() => {setIsLogin(false); setMessage('');}}
            >
              –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è
            </button>
            <button
              className={isLogin ? 'tab active' : 'tab'}
              onClick={() => {setIsLogin(true); setMessage('');}}
            >
              –í—Ö–æ–¥
            </button>
          </div>

          <form onSubmit={isLogin ? handleLogin : handleRegister} className="form">
            {!isLogin && (
              <input
                type="text"
                name="username"
                placeholder="–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è"
                value={formData.username}
                onChange={handleInputChange}
                className="input-field"
                required
              />
            )}

            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleInputChange}
              className="input-field"
              required
            />

            <input
              type="password"
              name="password"
              placeholder="–ü–∞—Ä–æ–ª—å"
              value={formData.password}
              onChange={handleInputChange}
              className="input-field"
              required
            />

            {!isLogin && (
              <input
                type="password"
                name="confirmPassword"
                placeholder="–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                className="input-field"
                required
              />
            )}

            {message && (
              <div className={`status-message ${message.includes('‚úÖ') ? 'success' : 'error'}`}>
                {message}
              </div>
            )}

            <button type="submit" className="submit-button">
              {isLogin ? '–í–æ–π—Ç–∏' : '–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è'}
            </button>
          </form>
        </div>
      </header>
