    { name: "MAC-10 Neon Rider", price: 75, rarity: "mil-spec", image: "https://steamcommunity-a.akamaihd.net/economy/image/-9a81dlWLwJ2UUGcVs_nsVtzdOEdtWwKGZZLQHTxDZ7I56KU0Zwwo4NUX4oFJZEHLbXH5ApeO4YmlhxYQknCRvCo04DEVlxkKgpou7umelFFztH3fTxQ5dKJlY-0hPLmMoTdn39Q5Px9g-fE4bP5jVGwrktqZWDwI4OTdlQ_aQ3W8gC4x-bph8S7tZ2czXFj7nMl5CzZmh2zhE4eOuFthKbIVBw7g7XVHQ" }
  ];

  const handleInputChange = (e) => {
