    // –°–æ–∑–¥–∞–µ–º –º–∞—Å—Å–∏–≤ –¥–ª—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∏ —Å –≤—ã–∏–≥—Ä–∞–Ω–Ω—ã–º —Å–∫–∏–Ω–æ–º –≤ –∫–æ–Ω—Ü–µ
    const shuffledSkins = [...allSkins].sort(() => Math.random() - 0.5);
    const spinItemsArray = [];

    // –î–æ–±–∞–≤–ª—è–µ–º 15 —Å–ª—É—á–∞–π–Ω—ã—Ö —Å–∫–∏–Ω–æ–≤
    for (let i = 0; i < 15; i++) {
      spinItemsArray.push(shuffledSkins[i % shuffledSkins.length]);
    }

    // –í –ö–û–ù–ï–¶ –¥–æ–±–∞–≤–ª—è–µ–º –≤—ã–∏–≥—Ä–∞–Ω–Ω—ã–π —Å–∫–∏–Ω (–æ–Ω –±—É–¥–µ—Ç –≤ —Ü–µ–Ω—Ç—Ä–µ –ø–æ—Å–ª–µ –∞–Ω–∏–º–∞—Ü–∏–∏)
