      setWonItem(newItem);
      setInventory([...inventory, newItem]);

      // –ó–∞–∫–∞–Ω—á–∏–≤–∞–µ–º –∞–Ω–∏–º–∞—Ü–∏—é —á–µ—Ä–µ–∑ 2 —Å–µ–∫—É–Ω–¥—ã
