                  disabled={user.balance < caseItem.price || isOpening}
                >
                  {isOpening && openingCase?.id === caseItem.id ? '–û—Ç–∫—Ä—ã–≤–∞—é...' :
                   user.balance >= caseItem.price ? '–û—Ç–∫—Ä—ã—Ç—å –∫–µ–π—Å' : '–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤'}
